import { useState } from 'react';
import './DropDown.css';
import button from './imgs/button1.png'

const DropDown = ({ container }) => {

  const [clicked, setClicked] = useState(false);

  return (
    <>
      <button onClick={(e) => {
        if(clicked) {
          const ele = document.getElementsByClassName(container+'-clicked')[0];
          ele.className = container;
          setClicked(false);
        } else {
          const ele = document.getElementsByClassName(container)[0];
          ele.className = 'goals-container-clicked';
          setClicked(true);
        }
      }} className='button'><img src={button} alt='black triangle' className='image'></img></button>
    </>
  );
};

export default DropDown;
