import React, { useState, useEffect } from 'react';

const YearsCounter = ({ startDate }) => {
  // Function to calculate precise years
  const calculatePreciseYears = (start) => {
    const now = new Date();
    const startDate = new Date(start);

    const totalYears = now.getFullYear() - startDate.getFullYear();
    const totalMonths = (now.getFullYear() - startDate.getFullYear()) * 12 + (now.getMonth() - startDate.getMonth());

    // Calculate the precise fraction of the year
    const startDateThisYear = new Date(now.getFullYear(), startDate.getMonth(), startDate.getDate());
    const daysPassed = Math.max(0, (now - startDateThisYear) / (1000 * 60 * 60 * 24));
    const daysInYear = (now.getFullYear() % 4 === 0 && (now.getFullYear() % 100 !== 0 || now.getFullYear() % 400 === 0)) ? 366 : 365;
    const fraction = daysPassed / daysInYear;

    return totalYears + fraction;
  };

  const [years, setYears] = useState(calculatePreciseYears(startDate));

  useEffect(() => {
    const updateYears = () => {
      setYears(calculatePreciseYears(startDate));
    };

    // Update every day (in milliseconds)
    const interval = setInterval(updateYears, 24 * 60 * 60 * 1000); // approximately 1 day

    // Clear interval on component unmount
    return () => clearInterval(interval);
  }, [startDate]);

  return (
    <div>
      {new Date(startDate).getFullYear()}: {years.toFixed(4)} years old
    </div>
  );
};

export default YearsCounter;
